def tile(X, Z):
    for x in range(len(plan)):
        for z in range(len(plan[0])):
            if plan[z][x] == '3':
                blocks.place(GLOWSTONE, rpos(x+X,-1,z+Z))
ROW = 3
COL = 2
def picture():
    #blocks.place(GLOWSTONE, rpos(0,-1,0))
    #blocks.place(GLOWSTONE, rpos(60*ROW,-1,0))
    #blocks.place(GLOWSTONE, rpos(0,-1,60*COL))
    shapes.line(GLOWSTONE, rpos(0,-1,0), rpos(60*ROW,-1,0))
    shapes.line(GLOWSTONE, rpos(60*ROW,-1,60*COL), rpos(60*ROW,-1,0))
